n = int(input("Nhập số nguyên n (0 <= n <= 9999): "))
n_str = ""
while n > 0:
    digit = n % 10
    if digit == 0:
        n_str = "A" + n_str
    elif digit == 1:
        n_str = "B" + n_str
    elif digit == 2:
        n_str = "C" + n_str
    elif digit == 3:
        n_str = "D" + n_str
    elif digit == 4:
        n_str = "E" + n_str
    elif digit == 5:
        n_str = "F" + n_str
    elif digit == 6:
        n_str = "G" + n_str
    elif digit == 7:
        n_str = "H" + n_str
    elif digit == 8:
        n_str = "K" + n_str
    elif digit == 9:
        n_str = "L" + n_str
    n //= 10
print("Số nguyên n được mã hóa thành:", n_str)
