def XetNgayKyDieu(ngay,thang,nam):
    if ngay*thang==nam%100: return True
    else: return False
ngay=int(input('ngay: '))
thang=int(input('thang: '))
nam=int(input('nam: '))
if XetNgayKyDieu(ngay,thang,nam): print(f'ngay {ngay} thang {thang} nam {nam} la ngay ky dieu')
else: print(f'ngay {ngay} thang {thang} nam {nam} khong la ngay ky dieu')
