def nhap():
    n=int(input('n='))
    return n

def sodoi(n):
    if n < 1 or n > 12:
        return ''
    elif n == 1:
        return '1st'
    elif n == 2:
        return '2nd'
    elif n == 3:
        return '3rd'
    elif n == 11:
        return 'eleventh'
    elif n == 12:
        return 'twelfth'
    else:
        return str(n) + 'th'
def inkq(kq):
    print (kq)
    
n=nhap()
kq=sodoi(n)
inkq(kq)
