def Input():
    L=input()
    return list(L)
def PigLatin(L):
    NgA=['a', 'e', 'o', 'i', 'u']
    dau=['?','!','.']
    # S=[]
    if L[-1] in dau:
        x=L.pop(L[-1])
    if L[0].lower() in NgA:
        S=L+['way']+x
    elif L[0].lower()=='t' and L[1].lower()=='h':
        S=L[2:]+L[:2]+['ay']+x
    else:
        S=L[1:]+L[:1]+['ay']+x
    if L[0].isupper():
        S.lower()
        S[0]=S[0].upper()
    return ''.join(S)

print(PigLatin(Input()))
